const handleClick = () => {
    if (isClicked) {
      setImageSrc("/images/Contents/Icons/circle-check-regular.png");
        setIsClicked(false); // 초기 상태 false 일 땐 초기 상태 이미지 src
      } else {
        setImageSrc("/images/Contents/Icons/circle-check-solid.png");
        setIsClicked(true); // true일 땐 변경될 이미지 src
      }
  };
