import React, { useEffect, useCallback, useState } from "react";
import axios from "axios";
import './Kakao.css';

function Kakao() {
    const [markerData, setMarkerData] = useState([]);

    const loadMap = () => {
        const script = document.createElement("script");
        script.src = `//dapi.kakao.com/v2/maps/sdk.js?appkey=${process.env.REACT_APP_KAKAOMAP_API_KEY}&libraries=services,drawing`;
        script.async = true;
        script.onload = () => {
            mapscript(); // 스크립트가 로드된 후에 mapscript 함수를 실행합니다.
        };
        script.onerror = (error) => {
            console.error("Error loading Kakao maps script:", error);
        };
        document.body.appendChild(script);

        return () => {
            document.body.removeChild(script);
        };
    };

    const mapscript = useCallback(() => {
        //지도 표시
        if (window.kakao && window.kakao.maps) {
            var container = document.getElementById('map');
            var options = {
                center: new window.kakao.maps.LatLng(37.336382765356035, 127.26531823399158),
                level: 3
            };
            const map = new window.kakao.maps.Map(container, options);

            map.addOverlayMapTypeId(window.kakao.maps.MapTypeId.TRAFFIC); //실시간 교통
            var mapTypeControl = new window.kakao.maps.MapTypeControl(); //type controller
            map.addControl(mapTypeControl, window.kakao.maps.ControlPosition.RIGHT);

            // 지도 확대 축소를 제어할 수 있는  줌 컨트롤을 생성합니다
            var zoomControl = new window.kakao.maps.ZoomControl();
            map.addControl(zoomControl, window.kakao.maps.ControlPosition.RIGHT);

            var overlays = [];

            // 마커와 오버레이 추가
            const addMarkers = (data) => {
                overlays.forEach((overlay) => overlay.setMap(null));
                overlays = [];

                data.forEach((markerData) => {
                    var position = new window.kakao.maps.LatLng(markerData.lat, markerData.lng);
                    var content = `<div class="wrap"> 
                                    <div class="info"> 
                                        <div class="title"> 
                                            ${markerData.name}  
                                        </div> 
                                        <div class="body"> 
                                            <div class="desc"> 
                                                <div class="ellipsis">${markerData.address}</div> 
                                                <div class="jibun ellipsis">${markerData.name}는</div> 
                                                <div class="jibun ellipsis">${markerData.operatorName}회사에서 만든 충전기이다.</div> 
                                            </div> 
                                            <button class="img" id="bookmarkButton"></button> 
                                        </div> 
                                    </div> 
                                </div>`;

                    // 마커를 표시할 지도
                    var marker = new window.kakao.maps.Marker({
                        position: position,
                        map: map
                    });
                    // customize overlay
                    var overlay = new window.kakao.maps.CustomOverlay({
                        position: position,
                        content: content,
                        yAnchor: 1
                    });

                    window.kakao.maps.event.addListener(marker, "click", function () {
                        closeOverlays();
                        overlay.setMap(map);
                    });

                    overlays.push(overlay);
                });
            }

            //지도 클릭 시 모든 overlay 닫기
            window.kakao.maps.event.addListener(map, "click", function () {
                closeOverlays();
            });

            function closeOverlays() {
                overlays.forEach(function(overlay) {
                    overlay.setMap(null);
                });
            }

            window.kakao.maps.event.addListener(map, 'bounds_changed', function() {
                var bound = map.getBounds();
                var swLatlng = bound.getSouthWest();
                var neLatlng = bound.getNorthEast();

                console.log('남서쪽 위경도 : ' + swLatlng.toString() + ',' +
                '북동쪽 위경도 : ' + neLatlng.toString());

                //서버에 GET 요청
                const url = `${process.env.REACT_APP_SERVER_URL}/charger/search/marker?swLat=${swLatlng.getLat()}&swLng=${swLatlng.getLng()}&neLat=${neLatlng.getLat()}&neLng=${neLatlng.getLng()}`;
                axios.get(url)
                .then(response => {
                    console.log('서버 응답', response.data);
                    setMarkerData(response.data); // 상태 업데이트
                    addMarkers(response.data); // 새로운 데이터로 마커 업데이트
                })
                .catch(error => {
                    console.log('GET 요청 에러', error);
                });
            });

            // 초기 마커 데이터 추가
            addMarkers(markerData);
        }
    }, [markerData]);

    useEffect(() => {
        loadMap();
    }, []);

    return (
        <div id="map" style={{ width: "100%", height: "100vh" }}></div>
    );
}

export default Kakao;
