import React, { useEffect, useCallback, useState } from "react";
import './Kakao.css'
import axios from "axios";

function Kakao() {
    const [map, setMap] = useState(null);
    const [markerData, setMarkerData] = useState([]);

    const fetchMarkerData = useCallback(async () => {
        try {
            const response = await axios.get(`${process.env.REACT_APP_SERVER_URL}/charger/search/marker`);
            if (response.data.success) {
                setMarkerData(response.data.result);
            } else {
                console.error('서버 응답 실패:', response.data.message);
            }
        } catch (error) {
            console.error('GET 요청 에러:', error);
        }
    }, []);

    useEffect(() => {
        fetchMarkerData();
    }, [fetchMarkerData]);

    const mapscript = useCallback(() => {
        if (window.kakao && window.kakao.maps) {
            const mapContainer = document.getElementById('map');
            const mapOption = {
                center: new kakao.maps.LatLng(33.450701, 126.570667), // 지도의 중심좌표
                level: 3 // 지도의 확대 레벨
            };

            const mapInstance = new kakao.maps.Map(mapContainer, mapOption);
            setMap(mapInstance);

            const bounds = new kakao.maps.LatLngBounds();

            markerData.forEach((data) => {
                const position = new kakao.maps.LatLng(data.lat, data.lng);
                const marker = new kakao.maps.Marker({ position });
                marker.setMap(mapInstance);

                bounds.extend(position);
            });

            mapInstance.setBounds(bounds);
        }
    }, [markerData]);

    useEffect(() => {
        mapscript();

        const script = document.createElement("script");
        script.src = `//dapi.kakao.com/v2/maps/sdk.js?appkey=${process.env.REACT_APP_KAKAOMAP_API_KEY}&libraries=services,drawing`;
        script.async = true;
        script.onload = () => {
            mapscript();
        };
        script.onerror = (error) => {
            console.error("Error loading Kakao maps script:", error);
        };
        document.body.appendChild(script);

        return () => {
            document.body.removeChild(script);
        };
    }, [mapscript]);

    return (
        <div id="map" style={{ width: "100%", height: "100vh" }}></div>
    );
}

export default Kakao;
