import React, { useEffect, useState } from "react";
import './Kakao.css'
import axios from "axios";

function Kakao() {
    const [map, setMap] = useState(null);
    const [markerData, setMarkerData] = useState([]);

    useEffect(() => {
        const initMap = () => {
            if (window.kakao && window.kakao.maps) {
                const container = document.getElementById('map');
                const options = {
                    center: new window.kakao.maps.LatLng(37.336382765356035, 127.26531823399158),
                    level: 3
                };
                const newMap = new window.kakao.maps.Map(container, options);
                setMap(newMap);
            }
        };

        const fetchMarkerData = async () => {
            try {
                const response = await axios.get(`${process.env.REACT_APP_SERVER_URL}/charger/search/marker`);
                if (response.data.success) {
                    setMarkerData(response.data.result);
                } else {
                    console.error('서버 응답 실패:', response.data.message);
                }
            } catch (error) {
                console.error('GET 요청 에러:', error);
            }
        };

        initMap();
        fetchMarkerData();
    }, []);

    useEffect(() => {
        if (map && markerData.length > 0) {
            // 마커 이미지의 이미지 주소입니다
            var imageSrc = "https://t1.daumcdn.net/localimg/localimages/07/mapapidoc/markerStar.png"; 

            for (var i = 0; i < markerData.length; i++) {
                // 마커 이미지의 이미지 크기 입니다
                var imageSize = new window.kakao.maps.Size(24, 35); 

                // 마커 이미지를 생성합니다    
                var markerImage = new window.kakao.maps.MarkerImage(imageSrc, imageSize); 

                // 마커를 생성합니다
                var marker = new window.kakao.maps.Marker({
                    map: map, // 마커를 표시할 지도
                    position: new window.kakao.maps.LatLng(markerData[i].lat, markerData[i].lng), // 마커를 표시할 위치
                    title: markerData[i].title, // 마커의 타이틀, 마커에 마우스를 올리면 타이틀이 표시됩니다
                    image: markerImage // 마커 이미지 
                });
            }
        }
    }, [map, markerData]);

    return (
        <div id="map" style={{ width: "100%", height: "100vh" }}></div>
    );
}

export default Kakao;
