import React, { useEffect, useCallback, useState } from "react";
import axios from "axios";

function Kakao() {
    const [map, setMap] = useState(null);
    const [overlays, setOverlays] = useState([]);

    const closeOverlays = useCallback(() => {
        overlays.forEach(overlay => overlay.setMap(null));
    }, [overlays]);

    const mapscript = useCallback(() => {
        if (window.kakao && window.kakao.maps) {
            var container = document.getElementById('map');
            var options = {
                center: new window.kakao.maps.LatLng(37.336382765356035, 127.26531823399158),
                level: 3
            };
            const newMap = new window.kakao.maps.Map(container, options);
            setMap(newMap);

            newMap.addOverlayMapTypeId(window.kakao.maps.MapTypeId.TRAFFIC);
            var mapTypeControl = new window.kakao.maps.MapTypeControl();
            newMap.addControl(mapTypeControl, window.kakao.maps.ControlPosition.RIGHT);
            var zoomControl = new window.kakao.maps.ZoomControl();
            newMap.addControl(zoomControl, window.kakao.maps.ControlPosition.RIGHT);

            window.kakao.maps.event.addListener(newMap, "bounds_changed", function () {
                var bound = newMap.getBounds();
                var swLatlng = bound.getSouthWest();
                var neLatlng = bound.getNorthEast();

                const url = `${process.env.REACT_APP_SERVER_URL}/charger/search/marker?swLat=${swLatlng.getLat()}&swLng=${swLatlng.getLng()}&neLat=${neLatlng.getLat()}&neLng=${neLatlng.getLng()}`;
                axios.get(url)
                    .then(response => {
                        const markerData = response.data.result;
                        const newOverlays = markerData.map(data => {
                            const position = new window.kakao.maps.LatLng(data.lat, data.lng);
                            const content = `<div class="wrap"> 
                                <div class="info"> 
                                    <div class="title"> 
                                        ${data.name}  
                                    </div> 
                                    <div class="body"> 
                                        <div class="desc"> 
                                            <div class="ellipsis">${data.address}</div> 
                                            <div class="jibun ellipsis">${data.name}는</div> 
                                            <div class="jibun ellipsis">${data.operatorName}회사에서 만든 충전기이다.</div> 
                                        </div> 
                                        <button class="img" id="bookmarkButton"></button> 
                                    </div> 
                                </div> 
                            </div>`;

                            const marker = new window.kakao.maps.Marker({
                                position: position,
                                map: newMap
                            });

                            const overlay = new window.kakao.maps.CustomOverlay({
                                position: position,
                                content: content,
                                yAnchor: 1
                            });

                            window.kakao.maps.event.addListener(marker, "click", function () {
                                closeOverlays();
                                overlay.setMap(newMap);
                            });

                            return overlay;
                        });

                        setOverlays(newOverlays);
                    })
                    .catch(error => {
                        console.log('GET 요청 에러', error);
                    });
            });
        }
    }, [closeOverlays]);

    useEffect(() => {
        mapscript();

        const script = document.createElement("script");
        script.src = `//dapi.kakao.com/v2/maps/sdk.js?appkey=${process.env.REACT_APP_KAKAOMAP_API_KEY}&libraries=services,drawing`;
        script.async = true;
        script.onload = () => {
            mapscript();
        };
        script.onerror = (error) => {
            console.error("Error loading Kakao maps script:", error);
        };
        document.body.appendChild(script);

        return () => {
            document.body.removeChild(script);
        };
    }, [mapscript]);

    useEffect(() => {
        if (map) {
            const points = overlays.map(overlay => overlay.getPosition());
            const bounds = new window.kakao.maps.LatLngBounds();
            points.forEach(point => bounds.extend(point));
            map.setBounds(bounds);
        }
    }, [map, overlays]);

    return (
        <div id="map" style={{ width: "100%", height: "100vh" }}></div>
    );
}

export default Kakao;
