import React, { useEffect, useCallback } from "react";
import './Kakao.css'
import axios from "axios";

function Kakao() {
    const mapscript = useCallback(async () => {
        //지도 표시
        if (window.kakao && window.kakao.maps) {
            var container = document.getElementById('map');
            var options = {
                center: new window.kakao.maps.LatLng(37.336382765356035, 127.26531823399158),
                level: 3
            };
            const map = new window.kakao.maps.Map(container, options);

            // 지도 확대 축소를 제어할 수 있는 줌 컨트롤을 생성합니다
            var zoomControl = new window.kakao.maps.ZoomControl();
            map.addControl(zoomControl, window.kakao.maps.ControlPosition.RIGHT);

            // 서버에서 마커 데이터 가져오기
            try {
                const response = await axios.get(`${process.env.REACT_APP_SERVER_URL}/charger/search/marker`);
                if (response.data.success) {
                    const markerData = response.data.result;
                    const points = markerData.map(data => ({
                        latlng: new window.kakao.maps.LatLng(data.lat, data.lng),
                        title: data.name
                    }));
                    var bounds = new window.kakao.maps.LatLngBounds();    
                    points.forEach(point => {
                        bounds.extend(point.latlng);

                        // 마커 이미지의 이미지 주소
                        var imageSrc = "https://t1.daumcdn.net/localimg/localimages/07/mapapidoc/markerStar.png"; 

                        // 마커 이미지의 이미지 크기
                        var imageSize = new window.kakao.maps.Size(24, 35); 

                        // 마커 이미지 생성
                        var markerImage = new window.kakao.maps.MarkerImage(imageSrc, imageSize); 

                        // 마커 생성
                         marker = new window.kakao.maps.Marker({
                            map: map,
                            position: point.latlng,
                            title: point.title,
                            image: markerImage
                        });
                    });
                    map.setBounds(bounds);
                    console.log('마커 데이터:', markerData); // 마커 데이터 로그 추가
                } else {
                    console.error('서버 응답 실패:', response.data.message);
                }
            } catch (error) {
                console.error('GET 요청 에러:', error);
            }

            window.kakao.maps.event.addListener(map, 'idle', function() {
                var bound = map.getBounds();
                var swLatlng = bound.getSouthWest();
                var neLatlng = bound.getNorthEast();

                console.log('<p>남서쪽 위경도 : ' + swLatlng.toString() + ',' + 
                '북동쪽 위경도 : '+ neLatlng.toString());

                // 서버에 GET 요청
                const url = `${process.env.REACT_APP_SERVER_URL}/charger/search/marker?swLat=${swLatlng.getLat()}&swLng=${swLatlng.getLng()}&neLat=${neLatlng.getLat()}&neLng=${neLatlng.getLng()}`;
                axios.get(url)
                .then(response => {
                    console.log('서버 응답', response.data);
                })
                .catch(error => {
                    console.log('GET 요청 에러', error);
                });
            });
        }
    }, []);

    useEffect(() => {
        mapscript();

        const script = document.createElement("script");
        script.src = `//dapi.kakao.com/v2/maps/sdk.js?appkey=${process.env.REACT_APP_KAKAOMAP_API_KEY}&libraries=services,drawing`;
        script.async = true;
        script.onload = () => {
            mapscript();
        };
        script.onerror = (error) => {
            console.error("Error loading Kakao maps script:", error);
        };
        document.body.appendChild(script);

        return () => {
            document.body.removeChild(script);
        };
    }, [mapscript]);

    return (
        <div id="map" style={{ width: "100%", height: "100vh" }}></div>
    );
}

export default Kakao;
