export default function Header({ onSearch }) {
  const [favoritelocClicked, setFavoritelocClicked] = useState(false);
  const [searchBarClicked, setSearchBarClicked] = useState(false);
  const [searchKeyword, setSearchKeyword] = useState('');

  const toggleFavoriteloc = () => {
    setFavoritelocClicked(!favoritelocClicked);
  };

  const toggleSearchBar = () => {
    setSearchBarClicked(!searchBarClicked);
  };

  const handleSearch = () => {
    onSearch(searchKeyword);
    setSearchKeyword('');
  };

  return (
    <header>
      <div>
        <nav>
          <ul>
            <li>
              <img src={searchBarClicked ? SearchBarClickedIcon : SearchBarIcon} alt="SearchBar" onClick={toggleSearchBar} />
            </li>
            <li>
              <button className="favorite-loc-button" onClick={toggleFavoriteloc}>
                <img src={favoritelocClicked ? FavoritelocClickedIcon : FavoritelocIcon} alt="Favoriteloc" />
              </button>
            </li>
          </ul>
        </nav>
        <div className={searchBarClicked ? 'search-bar-clicked' : 'search-block'}>
          <img src={GlassesIcon} alt="Search" className="glasses-icon" onClick={handleSearch} />
          <input
            type="text"
            placeholder="장소, 위치, 대중교통을 입력하세요"
            value={searchKeyword}
            onChange={(e) => setSearchKeyword(e.target.value)}
          />
        </div>
      </div>
    </header>
  );
}
