import React, { useEffect } from "react";
import { markerdata } from "./Data/markerData";
import './Kakao.css'

function Kakao() {
    useEffect(() => {
        const script = document.createElement("script");
        script.src = `//dapi.kakao.com/v2/maps/sdk.js?appkey=${process.env.REACT_APP_KAKAOMAP_API_KEY}&libraries=services,drawing`;
        script.async = true;
        script.onload = () => {
            mapscript();
        };
        script.onerror = (error) => {
            console.error("Error loading Kakao maps script:", error);
        };
        document.body.appendChild(script);

        return () => {
            document.body.removeChild(script);
        };
    }, []);

    const mapscript = () => {
        //지도 표시
        if (window.kakao && window.kakao.maps) {
            var container = document.getElementById('map');
            var options = {
                center: new window.kakao.maps.LatLng(37.336382765356035, 127.26531823399158),
                level: 3
            };
            const map = new window.kakao.maps.Map(container, options);

            markerdata.forEach((data) => {
                var infowindowArray = [];
                var positions = new window.kakao.maps.LatLng(data.lat, data.lng);
                var content = '<div class="wrap">' + 
                            '    <div class="info">' + 
                            '        <div class="title">' + 
                            '            ${data.name}' + 
                            '            <div class="close" onclick="closeOverlay()" title="닫기"></div>' + 
                            '        </div>' + 
                            '        <div class="body">' + 
                            '            <div class="img">' +
                            '                <img src="https://cfile181.uf.daum.net/image/250649365602043421936D" width="73" height="70">' +
                            '           </div>' + 
                            '            <div class="desc">' + 
                            '                <div class="ellipsis">${data.address}</div>' + 
                            '                <div class="jibun ellipsis"> 한국외국어대학교 글로벌 캠퍼스는</div>' + 
                            '                <div class="jibun ellipsis"> 용인에 위치한 대단한 학교다. </div>' + 
                            '            </div>' + 
                            '        </div>' + 
                            '    </div>' +    
                            '</div>';

                for (var i = 0; i < positions.length; i++) {
                    // 마커를 표시할 지도
                    var marker = new window.kakao.maps.Marker({
                        map: map,
                        position: positions[i].lating,
                    });

                    var infowindow = new window.kakao.maps.InfoWindow({
                        content: positions[i].content,
                        disableAutoPan: true,   // 인포윈도우를 열 때 지도가 자동으로 패닝하지 않을 지 여부(default: false)
                    });

                    infowindowArray.push(infowindow);

                    window.kakao.maps.event.addListener(
                        marker,
                        "click",
                        makeOverListener(map,marker,infowindow)
                    );
                    window.kakao.maps.addListener(map, "click", makeOutListener(infowindow));
                }

                function closeInfoWindow() {
                    for (infowindow of infowindowArray) {
                        infowindow.close();
                    }
                }
                function makeOverListener(map, marker, infowindow) {
                    return function () {
                        closeInfoWindow();
                        infowindow.open(map, marker);
                    }
                }
            });
        }
    };

    return (
        <div id="map" style={{ width: "1194px", height: "834px" }}></div>
    );
}

export default Kakao;
