// Kakao.js
import React, { useEffect } from "react";
import { useNavigate } from "react-router-dom";
import './Kakao.css';

function Kakao({ items }) {
  const navigate = useNavigate();

  useEffect(() => {
    const script = document.createElement("script");
    script.src = `//dapi.kakao.com/v2/maps/sdk.js?appkey=${process.env.REACT_APP_KAKAOMAP_API_KEY}&libraries=services,drawing`;
    script.async = true;
    script.onload = () => {
      const kakao = window.kakao;
      const mapContainer = document.getElementById('map');
      const mapOption = { 
        center: new kakao.maps.LatLng(37.5665, 126.9780), // 서울의 중심좌표
        level: 5 // 지도의 확대 레벨
      };
      const map = new kakao.maps.Map(mapContainer, mapOption);

      items.forEach(item => {
        const markerPosition  = new kakao.maps.LatLng(item.lat, item.lng); 
        const marker = new kakao.maps.Marker({
          position: markerPosition
        });
        marker.setMap(map);

        kakao.maps.event.addListener(marker, 'click', () => {
          navigate(`/detail/${item.stationChargerId}`);
        });
      });
    };
    script.onerror = (error) => {
      console.error("Error loading Kakao maps script:", error);
    };
    document.body.appendChild(script);

    return () => {
      document.body.removeChild(script);
    };
  }, [items, navigate]);

  return (
    <div id="map" style={{ width: "100%", height: "100vh" }}></div>
  );
}

export default Kakao;
