function toggleBookmark(event) {
    event.stopImmediatePropagation();

    // 북마크 버튼 엘리먼트 선택
    var bookmarkButton = event.target;

    // 이미지 변경
    var currentImage = bookmarkButton.style.backgroundImage;
    if (currentImage.includes('bookmarked-image')) {
        // 현재 북마크된 이미지일 때
        bookmarkButton.style.backgroundImage = 'url(./SVG/bookmark_clicked.svg)';
    } else {
        // 현재 북마크되지 않은 이미지일 때
        bookmarkButton.style.backgroundImage = 'url(./SVG/bookmark.svg)';
    }
}
