import { useState, useEffect } from 'react';
import React from 'react';
import './Bookmark.css'; // 북마크 스타일시트

// header.js에 있는 북마크 여는 버튼
export function OpenBookmarkList({ id, onClick, active, itemInfo }) {
  const handleClick = () => {
    onClick(id, itemInfo);
  }
  return (
    <button
      className={`bookmark-button ${active ? 'active' : ''}`}
      onClick={handleClick}
    >
      <div className="bookmark-icon">북마크</div>
    </button>
  );
}

// detail.js에 있는 북마크 추가하는 버튼
export function AddBookmarkButton({ id, bookmarkInfo }) {
  const [isBookmarked, setIsBookmarked] = useState(false);

  useEffect(() => {
    // id가 null이 아닌 경우에만 실행
    if (id) {
      const bookmarks = JSON.parse(localStorage.getItem('bookmarks')) || [];
      setIsBookmarked(bookmarks.some(bookmark => bookmark.id === id));
    }
  }, [id]);

  const handleAddBookmark = () => {
    if (!id) return; // id가 null인 경우 빠르게 반환

    const bookmarks = JSON.parse(localStorage.getItem('bookmarks')) || [];
    let updatedBookmarks;

    if (isBookmarked) {
      updatedBookmarks = bookmarks.filter(bookmark => bookmark.id !== id);
    } else {
      const newBookmark = {
        id,
        ...bookmarkInfo
      };
      updatedBookmarks = [...bookmarks, newBookmark];
    }

    localStorage.setItem('bookmarks', JSON.stringify(updatedBookmarks));
    setIsBookmarked(!isBookmarked);
    console.log(`Bookmark updated: ${!isBookmarked}`);
  };

  return (
    <button 
      className={`isBookmark-button ${isBookmarked ? 'active' : ''}`} 
      onClick={handleAddBookmark}
    />
  );
}

// bookmarklist에 있는 block
export function BookmarkList({ active, onClose, onItemClick }) {
  const [bookmarks, setBookmarks] = useState([]); // bookmarks 상태 추가

  useEffect(() => {
    const storedBookmarks = JSON.parse(localStorage.getItem('bookmarks')) || [];
    setBookmarks(storedBookmarks);
  }, []);

  const handleItemClick = (item) => {
    onItemClick(item); // 클릭된 아이템의 정보를 부모 컴포넌트로 전달
  };

  return (
    <div className={`bookmark-block ${active ? 'active' : ''}`}>
      <div className="bookmark-content">
        <button className="bookmark-close" onClick={onClose}>북마크 닫기</button>
        <ul>
          {bookmarks.map((bookmarkInfo, index) => (
            <li key={index} onClick={() => handleItemClick(bookmarkInfo)}>
              {bookmarkInfo && (
                <>
                  <span>{bookmarkInfo.name}</span>
                  <span>{bookmarkInfo.address}</span>
                </>
              )}
              <button>삭제</button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}
