//Detail.js
import React from 'react';
import { useParams } from 'react-router-dom';
import { dummyData } from './dummyData';
import './Detail.css'; // 스타일시트 임포트

function Detail() {
  const { id } = useParams();
  const data = dummyData.result.find(item => item.stationChargerId === id); // id를 이용해 데이터를 찾음

  if (!data) {
    return <div>데이터를 찾을 수 없습니다.</div>;
  }

  return (
    <div className="detail-block"> {/* 검색 결과와 같은 레벨에 나타나도록 수정 */}
      <div className="detail">
        <h1>{data.name}</h1>
        <p>{data.address}</p>
        <p>운영자: {data.operatorName}</p>
        <p>출력: {data.output}</p>
        <p>종류: {data.chargerTypeName}</p>
        <p>업데이트 날짜: {data.updatedAt}</p>
      </div>
    </div>
  );
}

export default Detail;
