const handleClick = () => {
    if (isClicked) {
      setImageSrc("./SVG/bookmark.svg");
        setIsClicked(false); // 초기 상태 false 일 땐 초기 상태 이미지 src
      } else {
        setImageSrc("./SVG/bookmark_clicked.svg");
        setIsClicked(true); // true일 땐 변경될 이미지 src
      }
  };
