import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';
import './Header.css'; // 스타일시트 임포트
import SearchBarIcon from '../component/SVG/searchbar.svg'; // 검색바 아이콘
import GlassesClickedIcon from '../component/SVG/glasses_clicked.svg'; // 클릭된 검색 아이콘

export default function Header({ onSearch }) {
  const [bookmarkClicked, setBookmarkClicked] = useState(false);
  const [searchBarClicked, setSearchBarClicked] = useState(false);
  const [searchKeyword, setSearchKeyword] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [isSearching, setIsSearching] = useState(false);
  const [noResults, setNoResults] = useState(false);
  const [currentPage, setCurrentPage] = useState(1);
  const resultsPerPage = 4;
  const totalPages = Math.ceil(searchResults.length / resultsPerPage);
  const searchBlockRef = useRef(null); // 검색창 요소에 대한 참조 생성

  useEffect(() => {
    // ESC 키를 눌렀을 때 검색창 닫기
    const handleKeyDown = (event) => {
      if (event.key === 'Escape') {
        setSearchBarClicked(false);
      }
    };

    document.addEventListener('keydown', handleKeyDown); // 이벤트 리스너 등록

    // 컴포넌트가 언마운트될 때 이벤트 리스너 제거
    return () => {
      document.removeEventListener('keydown', handleKeyDown);
    };
  }, []);

  const handleNextPage = () => {
    setCurrentPage((prevPage) => Math.min(prevPage + 1, totalPages));
  };

  const handlePrevPage = () => {
    setCurrentPage((prevPage) => Math.max(prevPage - 1, 1));
  };

  const handleFirstPage = () => {
    setCurrentPage(1);
  };

  const handleLastPage = () => {
    setCurrentPage(totalPages);
  };

  const handlePageClick = (page) => {
    setCurrentPage(page);
  };

  const renderPageNumbers = () => {
    const pages = [];
    const visiblePages = 10;

    for (let page = 1; page <= totalPages; page++) {
      pages.push(
        <button key={page} onClick={() => handlePageClick(page)} className={page === currentPage ? 'active' : ''}>
          {page}
        </button>
      );

      if (pages.length >= visiblePages) break;
    }

    return pages;
  };

  const toggleBookmark = () => {
    setBookmarkClicked(!bookmarkClicked);
  };

  const handleSearch = async () => {
    setIsSearching(true);
    setNoResults(false);

    // 검색 함수 호출
    onSearch(searchKeyword);

    try {
      const serverUrl = process.env.REACT_APP_SERVER_URL;
      if (!serverUrl) {
        throw new Error('Server URL is not defined');
      }
      const requestUrl = `${serverUrl}/charger/search?keyword=${encodeURIComponent(searchKeyword)}`;

      const response = await axios.get(requestUrl);

      const data = response.data;
      const results = data.result || [];
      setSearchResults(results);
      setNoResults(results.length === 0);
    } catch (error) {
      console.error('Error fetching search results:', error);
      setSearchResults([]);
      setNoResults(true);
    }

    setSearchKeyword('');
    setIsSearching(false);
    setCurrentPage(1);
  };

  // 컴포넌트 렌더링
  return (
    <header>
      <div>
        <nav>
          <ul>
            <li>
              <img src={SearchBarIcon} alt="SearchBar" onMouseEnter={() => setSearchBarClicked(true)} />
            </li>
            <li>
              <button className={`bookmark-button ${bookmarkClicked ? 'active' : ''}`} onClick={toggleBookmark}>
                <div className="bookmark-icon">북마크</div>
              </button>
            </li>
          </ul>
        </nav>
        <div className="search-block" style={{ display: searchBarClicked ? 'block' : 'none' }} ref={searchBlockRef}>
          <div className="searchbar_clicked">
            <div className="searchbar">
              <img src={GlassesClickedIcon} alt="Search" className="glassesclicked-icon" onClick={handleSearch} />
              <input
                type="text"
                id="search-input"
                name="search-input"
                placeholder="장소, 위치, 대중교통 검색"
                value={searchKeyword}
                onFocus={() => setSearchBarClicked(true)}
                onChange={(e) => setSearchKeyword(e.target.value)}
              />
            </div>
            <div className="searching-box">
              {isSearching && <div>검색 중...</div>}
              {!isSearching && noResults && <div>검색 결과가 없습니다.</div>}
              {!isSearching &&
                searchResults
                  .slice((currentPage - 1) * resultsPerPage, currentPage * resultsPerPage)
                  .map((item, index) => (
                    <div key={index} className="searching-item">
                      <p className="searching-item-name">{item.name}</p>
                      <p className="searching-item-address">{item.address}</p>
                    </div>
                  ))}
            </div>
            <div className="page-bar">
              <button onClick={handleFirstPage}>«</button>
              <button onClick={handlePrevPage}>‹</button>
              {renderPageNumbers()}
              <button onClick={handleNextPage}>›</button>
              <button onClick={handleLastPage}>»</button>
            </div>
          </div>
        </div>
      </div>
    </header>
  );
}
