import React, { useEffect, useCallback, useState } from "react";
import './Kakao.css';
import axios from "axios";

function Kakao() {
    const [markers, setMarkers] = useState([]);
    const [map, setMap] = useState(null);
    const [overlays, setOverlays] = useState([]);

    const fetchData = useCallback(async (swLat, swLng, neLat, neLng) => {
        try {
            const url = `${process.env.REACT_APP_SERVER_URL}/charger/search/marker?swLat=${swLat}&swLng=${swLng}&neLat=${neLat}&neLng=${neLng}`;
            const response = await axios.get(url);
            if (response.data && response.data.result) {
                return response.data.result;
            }
            return [];
        } catch (error) {
            console.error("Error fetching marker data:", error);
            return [];
        }
    }, []);

    const addMarkers = useCallback(async () => {
        if (!map) return;

        const bound = map.getBounds();
        const swLatlng = bound.getSouthWest();
        const neLatlng = bound.getNorthEast();

        console.log('<p>남서쪽 위경도 : ' + swLatlng.toString() + ',' +
            '북동쪽 위경도 : ' + neLatlng.toString());

        const fetchedMarkers = await fetchData(swLatlng.getLat(), swLatlng.getLng(), neLatlng.getLat(), neLatlng.getLng());
        const newOverlays = [];

        fetchedMarkers.forEach(data => {
            const position = new window.kakao.maps.LatLng(data.lat, data.lng);
            const content = `<div class="wrap"> 
                                <div class="info"> 
                                    <div class="title"> 
                                        ${data.name}  
                                    </div> 
                                    <div class="body"> 
                                        <div class="desc"> 
                                            <div class="ellipsis">${data.address}</div> 
                                            <div class="jibun ellipsis">${data.name}는</div> 
                                            <div class="jibun ellipsis">${data.operatorName}회사에서 만든 충전기이다.</div> 
                                        </div> 
                                        <button class="img" id="bookmarkButton"></button> 
                                    </div> 
                                </div> 
                            </div>`;
            const marker = new window.kakao.maps.Marker({
                position: position,
                map: map
            });
            const overlay = new window.kakao.maps.CustomOverlay({
                position: position,
                content: content,
                yAnchor: 1
            });

            window.kakao.maps.event.addListener(marker, "click", function () {
                closeOverlays();
                overlay.setMap(map);
            });
            newOverlays.push(overlay);
        });

        setOverlays(newOverlays);
    }, [map, fetchData]);

    const mapscript = useCallback(() => {
        if (window.kakao && window.kakao.maps) {
            var container = document.getElementById('map');
            var options = {
                center: new window.kakao.maps.LatLng(37.336382765356035, 127.26531823399158),
                level: 3
            };
            const newMap = new window.kakao.maps.Map(container, options);

            newMap.addOverlayMapTypeId(window.kakao.maps.MapTypeId.TRAFFIC);
            var mapTypeControl = new window.kakao.maps.MapTypeControl();
            newMap.addControl(mapTypeControl, window.kakao.maps.ControlPosition.RIGHT);

            var zoomControl = new window.kakao.maps.ZoomControl();
            newMap.addControl(zoomControl, window.kakao.maps.ControlPosition.RIGHT);

            setMap(newMap);
        }
    }, []);

    useEffect(() => {
        mapscript();

        const script = document.createElement("script");
        script.src = `//dapi.kakao.com/v2/maps/sdk.js?appkey=${process.env.REACT_APP_KAKAOMAP_API_KEY}&libraries=services,drawing`;
        script.async = true;
        script.onload = () => {
            mapscript();
        };
        script.onerror = (error) => {
            console.error("Error loading Kakao maps script:", error);
        };
        document.body.appendChild(script);

        return () => {
            document.body.removeChild(script);
        };
    }, [mapscript]);

    useEffect(() => {
        if (map) {
            const boundsChangedListener = window.kakao.maps.event.addListener(map, 'bounds_changed', addMarkers);

            return () => {
                window.kakao.maps.event.removeListener(boundsChangedListener);
            };
        }
    }, [map, addMarkers]);

    const closeOverlays = useCallback(() => {
        overlays.forEach(function (overlay) {
            overlay.setMap(null);
        });
    }, [overlays]);

    return (
        <div id="map" style={{ width: "100%", height: "100vh" }}></div>
    );
}

export default Kakao;
