import React, { useState } from 'react';
import './Bookmark.css'; // 북마크 스타일시트

export default function Bookmark() {
  const [bookmarkClicked, setBookmarkClicked] = useState(false);

  const toggleBookmark = () => {
    setBookmarkClicked(!bookmarkClicked);
  };

  return (
    <div>
      <button className={`bookmark-button ${bookmarkClicked ? 'active' : ''}`} onClick={toggleBookmark}>
        <div className="bookmark-icon">북마크</div>
      </button>
      <div className={`bookmark-block ${bookmarkClicked ? 'active' : ''}`}>
        <div className="bookmark-content">
          <button className="bookmark-close" onClick={toggleBookmark}>북마크 닫기</button>
          <div className="bookmark-items">
            <p>북마크된 아이템들</p>
            {/* 여기에 북마크 아이템들을 추가하세요 */}
          </div>
        </div>
      </div>
    </div>
  );
}
