import React, { useEffect, useCallback, useState } from "react";
import axios from "axios";
import './Kakao.css';

function Kakao() {
    const [allMarkerData, setAllMarkerData] = useState([]);
    const [map, setMap] = useState(null);
    const [overlays, setOverlays] = useState([]);

    const loadMap = useCallback(() => {
        const script = document.createElement("script");
        script.src = `//dapi.kakao.com/v2/maps/sdk.js?appkey=${process.env.REACT_APP_KAKAOMAP_API_KEY}&libraries=services,drawing`;
        script.async = true;
        script.onload = () => {
            mapscript();
        };
        script.onerror = (error) => {
            console.error("Error loading Kakao maps script:", error);
        };
        document.body.appendChild(script);

        return () => {
            document.body.removeChild(script);
        };
    }, []);

    const mapscript = useCallback(() => {
        if (window.kakao && window.kakao.maps) {
            var container = document.getElementById('map');
            var options = {
                center: new window.kakao.maps.LatLng(37.336382765356035, 127.26531823399158),
                level: 3
            };
            const mapInstance = new window.kakao.maps.Map(container, options);
            setMap(mapInstance);

            mapInstance.addOverlayMapTypeId(window.kakao.maps.MapTypeId.TRAFFIC);
            var mapTypeControl = new window.kakao.maps.MapTypeControl();
            mapInstance.addControl(mapTypeControl, window.kakao.maps.ControlPosition.RIGHT);

            var zoomControl = new window.kakao.maps.ZoomControl();
            mapInstance.addControl(zoomControl, window.kakao.maps.ControlPosition.RIGHT);

            window.kakao.maps.event.addListener(mapInstance, "click", closeOverlays);
            window.kakao.maps.event.addListener(mapInstance, 'bounds_changed', function () {
                updateMarkers(mapInstance);
            });
        }
    }, []);

    const updateMarkers = useCallback((mapInstance) => {
        if (!mapInstance) return;

        const bounds = mapInstance.getBounds();
        const swLatlng = bounds.getSouthWest();
        const neLatlng = bounds.getNorthEast();

        const visibleMarkers = allMarkerData.filter(marker => {
            return marker.lat >= swLatlng.getLat() && marker.lat <= neLatlng.getLat() &&
                marker.lng >= swLatlng.getLng() && marker.lng <= neLatlng.getLng();
        });

        overlays.forEach((overlay) => overlay.setMap(null)); // 기존 오버레이 제거
        const newOverlays = visibleMarkers.map((markerData) => {
            var position = new window.kakao.maps.LatLng(markerData.lat, markerData.lng);
            var content = `<div class="wrap"> 
                            <div class="info"> 
                                <div class="title"> 
                                    ${markerData.name}  
                                </div> 
                                <div class="body"> 
                                    <div class="desc"> 
                                        <div class="ellipsis">${markerData.address}</div> 
                                        <div class="jibun ellipsis">${markerData.name}는</div> 
                                        <div class="jibun ellipsis">${markerData.operatorName || 'Unknown'} 회사에서 만든 충전기이다.</div> 
                                    </div> 
                                    <button class="img" id="bookmarkButton"></button> 
                                </div> 
                            </div> 
                        </div>`;

            var marker = new window.kakao.maps.Marker({
                position: position,
                map: mapInstance
            });

            var overlay = new window.kakao.maps.CustomOverlay({
                position: position,
                content: content,
                yAnchor: 1
            });

            window.kakao.maps.event.addListener(marker, "click", function () {
                closeOverlays();
                overlay.setMap(mapInstance);
            });

            return overlay;
        });

        setOverlays(newOverlays);
    }, [allMarkerData, overlays]);

    const closeOverlays = () => {
        overlays.forEach((overlay) => overlay.setMap(null));
    };

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get(`${process.env.REACT_APP_SERVER_URL}/charger/all`);
                if (response.data.success) {
                    setAllMarkerData(response.data.result); // 전체 마커 데이터 저장
                } else {
                    console.log('서버 응답 실패', response.data.message);
                }
            } catch (error) {
                console.log('GET 요청 에러', error);
            }
        };

        loadMap();
        fetchData();
    }, [loadMap]);

    return (
        <div id="map" style={{ width: "100%", height: "100vh" }}></div>
    );
}

export default Kakao;
