import React, { useState } from 'react';
import './Header.css';
import SearchBarIcon from '../component/searchbar.svg';
import SearchBarClickedIcon from '../component/searchbar_clicked.svg';

export default function Header({ onSearch }) {
  const [searchBarClicked, setSearchBarClicked] = useState(false);
  const [keyword, setKeyword] = useState('');

  const toggleSearchBar = () => {
    setSearchBarClicked(!searchBarClicked);
  };

  const hideSearchBlock = () => {
    setSearchBarClicked(false);
    setKeyword('');
  };

  const handleInputChange = (e) => {
    setKeyword(e.target.value);
  };

  const handleSearch = () => {
    onSearch(keyword);
  };

  return (
    <header>
      <div>
        <nav>
          <ul>
            <li>
              <img src={searchBarClicked ? SearchBarClickedIcon : SearchBarIcon} alt="SearchBar" onClick={toggleSearchBar} />
            </li>
          </ul>
        </nav>
        {searchBarClicked && (
          <div className="search-block">
            <input type="text" placeholder="검색어를 입력하세요" value={keyword} onChange={handleInputChange} />
            <button onClick={handleSearch}>검색</button>
            <img src={SearchBarClickedIcon} alt="SearchBarClicked" className="search-bar-clicked" onClick={hideSearchBlock} />
          </div>
        )}
      </div>
    </header>
  );
}
