// Detail.js
import React from 'react';
import './Detail.css'; // 스타일시트 임포트

function Detail({ item, onClose }) {
  if (!item) {
    return null;
  }

  return (
    <div className="detail-block"> {/* 검색 결과와 같은 레벨에 나타나도록 수정 */}
      <div className="detail">
        <button className="detail-close" onClick={onClose}></button> {/* 닫기 버튼 추가 */}
        <img src='https://www.carguy.kr/news/photo/201804/32027_6843_1617.jpg' width={320}></img>
        <h1>{item.name}</h1>
        <p>{item.address}</p>
        <p>운영자: {item.operatorName}</p>
        <p>출력: {item.output}</p>
        <p>종류: {item.chargerTypeName}</p>
        <p>{item.kindDetailName}</p>
        <p>업데이트 날짜: {item.updatedAt}</p>
      </div>
    </div>
  );
}

export default Detail;
