import React, { useState, useEffect } from 'react';
import Header from './component/Header';
import Kakao from './component/Kakao';
import { BrowserRouter as Router } from 'react-router-dom';
import { BookmarkButton } from './component/bmkButton';
import { dummyData } from './component/dummyData';
import './App.css';

function App() {
  const [searching, setSearching] = useState(false);
  const [items, setItems] = useState([]);
  const { stationChargerId, name, address, operatorName, output } = dummyData.result;

  useEffect(() => {
    if (searching) {
      console.log('Searching...');
      // 검색 시작 시 필요한 로직 작성
      // 여기에서는 임시로 setTimeout을 사용하여 1초 후에 검색이 완료되었다고 가정하겠습니다.
      setTimeout(() => {
        setItems(['item1', 'item2', 'item3']); // 임시로 데이터 설정
        setSearching(false);
      }, 1000);
    }
  }, [searching]);

  const loadItems = (keyword) => {
    // 검색을 시작하면 searching 상태를 true로 변경하여 useEffect가 실행되도록 합니다.
    setSearching(true);
  };

  return (
    <Router>
      <div className='App'>
        <Header onSearch={(keyword) => loadItems(keyword)} />  
        <Kakao items={items}/>
        <div className="charger-info">
          <h2>{name}</h2>
          <p>{address}</p>
          <p>운영자: {operatorName}</p>
          <p>출력: {output}</p>
          <BookmarkButton id={stationChargerId} />
        </div>
      </div>
    </Router>
  );
}

export default App;
