// App.js
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Switch, Link } from 'react-router-dom';
import Header from './component/Header';
import Kakao from './component/Kakao';
import Detail from './Detail';
import { BookmarkButton } from './bmkButton';
import { dummyData } from './dummyData';
import './App.css';

function App() {
  const [searching, setSearching] = useState(false);
  const [items, setItems] = useState([]);
  const { stationChargerId, name, address, operatorName, output } = dummyData.result;

  useEffect(() => {
    if (searching) {
      console.log('Searching...');
      setTimeout(() => {
        setItems([dummyData.result]); // 임시로 더미 데이터를 사용하여 검색 결과 설정
        setSearching(false);
      }, 1000);
    }
  }, [searching]);

  const loadItems = (keyword) => {
    setSearching(true);
  };

  return (
    <Router>
      <div className='App'>
        <Header onSearch={(keyword) => loadItems(keyword)} />
        <Switch>
          <Route exact path="/">
            <Kakao items={items} />
            <div className="charger-info">
              <h2>{name}</h2>
              <p>{address}</p>
              <p>운영자: {operatorName}</p>
              <p>출력: {output}</p>
              <BookmarkButton id={stationChargerId} />
              <Link to={`/detail/${stationChargerId}`}>
                <div className="searching-box">상세 정보 보기</div>
              </Link>
            </div>
          </Route>
          <Route path="/detail/:id" component={Detail} />
        </Switch>
      </div>
    </Router>
  );
}

export default App;
