import './Kakao.css'

export const createMarkerContent = (data, handleBookmarkClick) => {
    return (
        <div class="wrap"> 
        <div class="info"> 
          <div class="title"> 
            ${data.name}  
          </div> 
          <div class="body"> 
            <div class="desc"> 
              <div class="ellipsis">${data.address}</div> 
              <div class="jibun ellipsis">${data.chargerTypeName}</div> 
              <div class="jibun ellipsis">회사명 : ${data.operatorName}</div> 
              <div class="jibun ellipsis">충전용량 : ${data.outputName}</div> 
            </div> 
            <button class="img bookmark-button" onclick="(${handleBookmarkClick.toString()})('${data.id}')"></button> 
          </div> 
        </div> 
      </div>
    );
  };

  export const handleBookmarkClick = (id) => {
    console.log(`Bookmark clicked for item with id: ${id}`);
    // 여기서 북마크 데이터를 처리하는 로직을 추가하세요
    // 예를 들어, 로컬 스토리지에 저장하거나, 서버로 전송할 수 있습니다.
  };
