import './Kakao.css';

export const createMarkerContent = (data) => {
  return `
    <div class="wrap"> 
      <div class="info"> 
        <div class="title">${data.name}</div> 
        <div class="body"> 
          <div class="desc"> 
            <div class="ellipsis">${data.address}</div> 
            <div class="jibun ellipsis">${data.chargerTypeName}</div> 
            <div class="jibun ellipsis">회사명 : ${data.operatorName}</div> 
            <div class="jibun ellipsis">충전용량 : ${data.outputName}</div> 
          </div> 
          <button class="img bookmark-button" data-id="${data.id}">북마크</button> 
        </div> 
      </div> 
    </div>`;
};

export const handleBookmarkClick = (id) => {
  console.log(`Bookmark clicked for item with id: ${id}`);
  let bookmarks = JSON.parse(localStorage.getItem('bookmarks')) || [];
  if (bookmarks.includes(id)) {
    bookmarks = bookmarks.filter(bookmark => bookmark !== id);
  } else {
    bookmarks.push(id);
  }
  localStorage.setItem('bookmarks', JSON.stringify(bookmarks));
};
